// src/FileEditorWithBugTable.js
import React, { useRef, useState, useEffect } from 'react';
import { Button, Table, message } from 'antd';
import Editor from '@monaco-editor/react';

const FileEditorWithBugTable = ({ selectedFile, onSaveFile, setSelectedFile }) => {
  const editorRef = useRef(null);
  const [data, setData] = useState([]);
  const [tableHeight, setTableHeight] = useState(200);
  const [isResizing, setIsResizing] = useState(false);
  const [messages, setMessages] = useState([]);
  const socket = useRef(null);

  useEffect(() => {
    socket.current = new WebSocket('ws://localhost:3001');
    socket.current.onopen = () => setMessages((prev) => [...prev, 'Connected to server']);
    socket.current.onmessage = (event) => setMessages((prev) => [...prev, event.data]);
    socket.current.onclose = () => setMessages((prev) => [...prev, 'Disconnected from server']);
    socket.current.onerror = (error) => setMessages((prev) => [...prev, `WebSocket error: ${error.message}`]);

    return () => {
      if (socket.current) {
        socket.current.close();
      }
    };
  }, []);
  const handleEditorDidMount = (editor, monaco) => {
    editorRef.current = editor;
    editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyCode.KeyS, () => {
      if (onSaveFile) {
        onSaveFile(editorRef.current.getValue());
      }
    });
  };

  const handleCellClick = (location) => {
    const [lineNumber, colNumber] = location.match(/\d+/g).map(Number);
    if (editorRef.current) {
      editorRef.current.revealLineInCenter(lineNumber);
      editorRef.current.setPosition({ lineNumber, column: colNumber });
      editorRef.current.focus();
    }
  };

  const handleDelete = (key) => {
    const newData = data.filter((item) => item.key !== key);
    setData(newData);
  };

  const bugDetection = () => {
    const newContent = editorRef.current.getValue()
    if (selectedFile) {
        const updatedFile = { ...selectedFile, content: newContent };
        setSelectedFile(updatedFile);
        if (socket.current && socket.current.readyState === WebSocket.OPEN) {
          const message = JSON.stringify({
            content: updatedFile.content,
          });
          socket.current.send(message);
          message.success('File sent');
        }
    }
    setData([
      {
        key: '1',
        id: '1',
        type: 'PartialLeak',
        location: 'ln 4, cl 3',
      },
      {
        key: '2',
        id: '2',
        type: 'MemoryLeak',
        location: 'ln 8, cl 5',
      },
    ]);
  };

  const startResizing = (e) => {
    setIsResizing(true);
    document.body.style.cursor = 'ns-resize';
  };

  const stopResizing = () => {
    setIsResizing(false);
    document.body.style.cursor = 'default';
  };

  const resizeTable = (e) => {
    if (isResizing) {
      const newHeight = window.innerHeight - e.clientY;
      setTableHeight(newHeight);
    }
  };

  React.useEffect(() => {
    window.addEventListener('mousemove', resizeTable);
    window.addEventListener('mouseup', stopResizing);
    return () => {
      window.removeEventListener('mousemove', resizeTable);
      window.removeEventListener('mouseup', stopResizing);
    };
  }, [isResizing]);

  const columns = [
    {
      title: 'Id',
      dataIndex: 'id',
      key: 'id',
    },
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
    },
    {
      title: 'Location',
      dataIndex: 'location',
      key: 'location',
      onCell: (record) => ({
        onClick: () => handleCellClick(record.location),
      }),
    },
    {
      title: 'Confirm',
      key: 'confirm',
      render: (text, record) => (
        <Button type="link" onClick={() => handleDelete(record.key)}>
          Delete
        </Button>
      ),
    },
  ];

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
        <p style={{ marginRight: '10px' }}>{selectedFile.title}</p>
        <Button onClick={bugDetection}>漏洞检测</Button>
        <Button>补丁修复</Button>
      </div>
      <Editor
        height={`calc(100vh - ${tableHeight + 50}px)`}
        defaultLanguage="cpp"
        value={selectedFile.content}
        onMount={handleEditorDidMount}
      />
      <div style={{ height: `${tableHeight}px`, overflow: 'auto', marginTop: '20px', position: 'relative' }}>
        <Table columns={columns} dataSource={data} pagination={false} />
        <div
          style={{
            height: '5px',
            backgroundColor: 'gray',
            cursor: 'ns-resize',
            position: 'absolute',
            top: 0,
            width: '100%',
          }}
          onMouseDown={startResizing}
        />
      </div>
    </div>
  );
};

export default FileEditorWithBugTable;
